INSERT INTO `earth_posts` VALUES (194, 3, '2009-04-21 21:56:35', '2009-04-21 20:56:35', 'Here\'s an almost wordless month in pictures update. I\'m so far behind that I\'m cramming 3 months into one mosaic and as I didn\'t seem to make it to the plot much in those first 3 months, there isn\'t a lot to say.\r\n<a href="http://www.flickr.com/photos/warriorwomen/3460706990/" class="tt-flickr tt-flickr-Medium"><img src="http://farm4.static.flickr.com/3589/3460706990_1b0d61f72e.jpg" alt="Month in Pictures - Jan to Mar 2009" width="500" height="500" border="0" /></a> ', 'Months in Pictures - Jan to Mar 2009', 0, '', 'publish', 'open', 'open', '', 'months-in-pictures-jan-to-mar-2009', '', '', '2009-04-21 21:56:35', '2009-04-21 20:56:35', '', 0, 'http://www.earthwoman.co.uk/?p=194', 0, 'post', '', 1, ''); 
